package ControlStatement;

public class SwitchStudy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int year=6;
		switch (year)
		{
		case 1:  System.out.println("wl come in 1st year");
		break;

		case 2: System.out.println("wl come 2nd year");
		break;
			
		case 3: System.out.println("wl come 3rd year");	
		break;
			
		default: System.out.println("plz enter valid year");
		break;
		}
		
		
	}

}
